import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import {Route, Link, BrowserRouter as Router} from 'react-router-dom'


import {API_ROOT, HEADERS} from './constants/index.js'
import Canvas from './components/Canvas'
import GameWindow from './components/GameWindow'
import UserForm from './components/UserForm'
import CardContainer from './components/CardContainer'
import HeaderBar from './components/HeaderBar'


class App extends Component {

  rerenderHeader = () => {
    this.forceUpdate()
  }

  render() {
    return (
      <Router>
        <div className="App">

        <HeaderBar />

        <Route exact path="/" render={(props) => <UserForm {...props} rerenderHeader={this.rerenderHeader}/>} />
        <Route path="/draw" component={GameWindow} />
        <Route path="/gallery" component={CardContainer} />

        </div>
      </Router>

    );
  }

}

export default App;
