import React, {Component} from 'react'


class EditMessageForm extends Component {

  handleSubmit = (ev) => {
    ev.preventDefault()
    // use this.props.userId to make a fetch-PATCH request...
    this.props.patchMessage(ev.target.value)
  }

  render() {
    return (
      <form className="edit-message-form" onSubmit={this.handleSubmit}>
        <input type="text" placeholder={this.props.message.text} />
        <input type="submit" />
      </form>
    )
  }

}

export default EditMessageForm
