import React, {Component} from 'react'
import EditMessageForm from './EditMessageForm'


class Message extends Component {

  constructor() {
    super()
    this.edit = false;
    this.delete = false;
  }

  handleClickEdit = () => {
    this.edit = true
  }

  handleClickDelete = () => {

  }

  renderEditForm = () => {
    if (this.edit) {
      return <EditMessageForm message={this.props.message} userId={this.props.userId} />
    }
  }

  render() {
    return (
      <div className="message">
        <p>{this.props.name}: {this.props.text}</p>

        {this.renderEditForm()}

        <button className="edit-button" onClick={this.handleClickEdit}>Edit</button>
        <button className="delete-button" onClick={this.handleClickDelete}>Delete</button>
      </div>
    )
  }

}

export default Message
