import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import {Route, Link, BrowserRouter as Router} from 'react-router-dom'


import {API_ROOT, HEADERS} from './constants/index.js'
import Canvas from './components/Canvas'
import GameWindow from './components/GameWindow'
import UserForm from './components/UserForm'
import CardContainer from './components/CardContainer'
import HeaderBar from './components/HeaderBar'


class App extends Component {

  rerenderApp = () => {
    this.forceUpdate()
  }

  fetchUsers = () => {
    fetch(`${API_ROOT}/users`)
    .then(resp => resp.json())
    .then(data => {
      let obj = {}
      data.forEach(user => {
        obj[user.id] = user.name
      })
      this.setState({users: obj})
    })
  }

  render() {
    return (
      <Router>
        <div className="App">

        <HeaderBar />

        <Route exact path="/" render={(props) => <UserForm {...props} rerenderApp={this.rerenderApp} />} />
        <Route path="/draw" render={(props) => <GameWindow {...props} fetchUsers={this.fetchUsers} />} />
        <Route path="/gallery" component={CardContainer} />

        </div>
      </Router>

    );
  }

}

export default App;

